// TodoList.js
import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { FiPlus, FiRefreshCw } from 'react-icons/fi';
import { fetchTodos, addTodo } from '../store/todoSlice';
import TodoItem from './TodoItem';
import './TodoList.css';

const TodoList = () => {
  const { items, status, error } = useSelector((state) => state.todos);
  const dispatch = useDispatch();
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    dispatch(fetchTodos());
  }, [dispatch]);

  const handleAdd = (e) => {
    e.preventDefault();
    if (newTodo.trim()) {
      dispatch(addTodo(newTodo));
      setNewTodo('');
    }
  };

  const pendingTasks = items.filter(task => !task.completed);
  const completedTasks = items.filter(task => task.completed);

  return (
    <div className="todo-container">
      <form onSubmit={handleAdd} className="add-form">
        <div className="input-group">
          <input
            type="text"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
            placeholder="Add a new task..."
            className="add-input"
            aria-label="New task input"
          />
          <button type="submit" className="btn btn-primary" aria-label="Add task">
            <FiPlus className="icon" />
            <span>Add Task</span>
          </button>
        </div>
      </form>

      {status === 'loading' && (
        <div className="loading-state">
          <FiRefreshCw className="spin-icon" />
          <p>Loading tasks...</p>
        </div>
      )}

      {status === 'failed' && (
        <div className="error-state">
          <p>Error: {error}</p>
        </div>
      )}

      <div className="tasks-wrapper">
        {pendingTasks.length > 0 && (
          <section className="task-section">
            <h2 className="section-title">Pending ({pendingTasks.length})</h2>
            <div className="tasks-list">
              {pendingTasks.map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onDelete={id => dispatch(deleteTodo(id))}
                  onUpdate={(id, text) => dispatch(updateTodo({ id, text }))}
                  onComplete={(id, completed) => dispatch(updateTodo({ id, completed: !completed }))}
                />
              ))}
            </div>
          </section>
        )}

        {completedTasks.length > 0 && (
          <section className="task-section">
            <h2 className="section-title">Completed ({completedTasks.length})</h2>
            <div className="tasks-list">
              {completedTasks.map((todo) => (
                <TodoItem
                  key={todo.id}
                  todo={todo}
                  onDelete={id => dispatch(deleteTodo(id))}
                  onUpdate={(id, text) => dispatch(updateTodo({ id, text }))}
                  onComplete={(id, completed) => dispatch(updateTodo({ id, completed: !completed }))}
                />
              ))}
            </div>
          </section>
        )}
      </div>

      {items.length === 0 && status !== 'loading' && (
        <div className="empty-state">
          <p>No tasks found. Start by adding a new task!</p>
        </div>
      )}
    </div>
  );
};

export default TodoList;
