// TodoItem.js
import React, { useState } from 'react';
import { FiEdit, FiTrash2, FiCheckCircle, FiCircle } from 'react-icons/fi';
import './TodoItem.css';

const TodoItem = ({ todo, onDelete, onUpdate, onComplete }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [text, setText] = useState(todo.text);

  const handleSave = () => {
    if (text.trim()) {
      onUpdate(todo.id, text);
      setIsEditing(false);
    }
  };

  return (
    <div className={`todo-item ${todo.completed ? 'completed' : ''}`}>
      <button 
        className="todo-check"
        onClick={() => onComplete(todo.id, !todo.completed)}
        aria-label={todo.completed ? 'Uncomplete task' : 'Complete task'}
      >
        {todo.completed ? (
          <FiCheckCircle className="icon check-icon" />
        ) : (
          <FiCircle className="icon check-icon" />
        )}
      </button>

      {isEditing ? (
        <input
          type="text"
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSave()}
          className="todo-edit-input"
          autoFocus
        />
      ) : (
        <span className="todo-text">{todo.text}</span>
      )}

      <div className="todo-actions">
        {isEditing ? (
          <button 
            className="btn btn-save"
            onClick={handleSave}
            aria-label="Save changes"
          >
            Save
          </button>
        ) : (
          <>
            <button
              className="btn btn-icon"
              onClick={() => setIsEditing(true)}
              aria-label="Edit task"
            >
              <FiEdit className="icon" />
            </button>
            <button
              className="btn btn-icon"
              onClick={() => onDelete(todo.id)}
              aria-label="Delete task"
            >
              <FiTrash2 className="icon" />
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default TodoItem;
